async function getServicios(){const e=await fetch("https://627ad05ab54fe6ee007e27f4.mockapi.io/servicios-barberia"),t=await e.json();console.log(t),mostarServicios(t)}function mostarServicios(e){const t=document.querySelector("#catalogo");e.forEach(e=>{const{nombre:o,precio:c}=e,i=document.createElement("DIV"),n=document.createElement("P"),a=document.createElement("P");i.classList.add("servicio"),n.textContent=o,n.classList.add("nombreServicio"),a.textContent=c,a.classList.add("precioServicio"),i.appendChild(n),i.appendChild(a),t.appendChild(i)})}document.addEventListener("DOMContentLoaded",()=>{getServicios()});