async function getServicios(){const e=await fetch("https://627ad05ab54fe6ee007e27f4.mockapi.io/servicios-barberia"),t=await e.json();console.log(t),mostarServicios(t),seleccionarServicio()}function mostarServicios(e){const t=document.querySelector("#catalogo");e.forEach(e=>{const{nombre:o,precio:i}=e,c=document.createElement("DIV"),s=document.createElement("P"),n=document.createElement("P");c.classList.add("servicio"),s.textContent=o,s.classList.add("nombreServicio"),n.textContent=`$ ${i}`,n.classList.add("precioServicio"),c.appendChild(s),c.appendChild(n),t.appendChild(c)})}document.addEventListener("DOMContentLoaded",()=>{getServicios()});let serviciosElegidos=[];function seleccionarServicio(){document.querySelectorAll(".servicio").forEach(e=>{e.addEventListener("click",()=>{e.classList.contains("selected")?(e.classList.remove("selected"),serviciosElegidos=serviciosElegidos.filter(t=>t[0]!==e.textContent.split("$ ")[0])):(e.classList.add("selected"),serviciosElegidos.unshift(e.textContent.split("$ ")));const t=document.querySelector(".btn-confirmarServicio");0==serviciosElegidos.length?t.classList.add("btn-apagado"):(t.classList.remove("btn-apagado"),calcTotal(),t.textContent=`Total: $${total}`),displayTurno(),cuentaServicios()})})}let total=0;function calcTotal(){const e=serviciosElegidos.map(e=>parseInt(e[1]));total=e.reduce((e,t)=>e+t)}function displayTurno(){const e=document.querySelector("#turnos .default"),t=document.querySelector("#turnos .solicitarTurnos");0===serviciosElegidos.length?(t.classList.add("hidden"),e.classList.remove("hidden")):(t.classList.remove("hidden"),e.classList.add("hidden"))}function cuentaServicios(){const e=document.querySelector("#cuenta-servicios");let t=e.childNodes.length;for(;t>0;t--)e.childNodes[t-1].remove();serviciosElegidos.forEach(t=>{const o=document.createElement("P");o.textContent=t[0],e.appendChild(o)});const o=document.createElement("A"),i=document.createElement("BUTTON");o.href="#servicios",i.textContent="Modificar Servicios",o.appendChild(i),e.appendChild(o)}